mapper.select:
SELECT USER_NUMBER, USER_ID, USER_NAME, USER_STATUS, STATUS_NAME FROM ( SELECT ROWNUM RN, USER_NUMBER, USER_ID, USER_NAME, USER_STATUS, STATUS_NAME FROM ( SELECT USER_NUMBER, USER_ID, USER_NAME, USER_STATUS, S.STATUS_NAME FROM TBL_USER_STATUS S JOIN TBL_USER U ON S.STATUS_NUMBER = U.USER_STATUS ORDER BY USER_NUMBER DESC )
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RN > (#{page} - 1) * #{amount}

mapper.update:
UPDATE TBL_USER
SET USER_STATUS = #{userStatus}
WHERE USER_NUMBER = #{userNumber}

mapper.select:
SELECT USER_NUMBER, USER_ID, USER_NAME, USER_STATUS, STATUS_NAME FROM ( SELECT ROWNUM RN, USER_NUMBER, USER_ID, USER_NAME, USER_STATUS, STATUS_NAME FROM ( SELECT USER_NUMBER, USER_ID, USER_NAME, USER_STATUS, S.STATUS_NAME FROM TBL_USER_STATUS S JOIN TBL_USER U ON S.STATUS_NUMBER = U.USER_STATUS WHERE USER_ID LIKE '%' || #{keyword} || '%' OR USER_NAME LIKE '%' || #{keyword} || '%' ORDER BY USER_NUMBER DESC )
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RN > (#{criteria.page} - 1) * #{criteria.amount}

SELECT STROLL_BOARD_NUMBER, STROLL_BOARD_TITLE, USER_ID, TO_CHAR(STROLL_BOARD_MEETING_DATE, 'YYYY-MM-DD') AS STROLL_BOARD_MEETING_DATE, STROLL_BOARD_ADMIN_DISTRICT FROM ( SELECT ROWNUM RN, STROLL_BOARD_NUMBER, STROLL_BOARD_TITLE, USER_ID, STROLL_BOARD_MEETING_DATE, STROLL_BOARD_ADMIN_DISTRICT FROM (SELECT STROLL_BOARD_NUMBER, STROLL_BOARD_TITLE, USER_ID, STROLL_BOARD_MEETING_DATE, STROLL_BOARD_ADMIN_DISTRICT FROM TBL_USER U JOIN TBL_STROLL_BOARD B ON U.USER_NUMBER = B.USER_NUMBER WHERE STROLL_BOARD_TITLE LIKE '%' || #{keyword} || '%' OR USER_ID LIKE '%' || #{keyword} || '%' ORDER BY STROLL_BOARD_NUMBER DESC )
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RN > (#{criteria.page} - 1) * #{criteria.amount}

SELECT APPLY_NUMBER, APPLY_CONTENT, USER_NAME, APPLY_DATE FROM ( SELECT ROWNUM RN, APPLY_NUMBER, APPLY_CONTENT, USER_NAME, APPLY_DATE FROM ( SELECT APPLY_NUMBER, APPLY_CONTENT, USER_NAME, APPLY_DATE FROM TBL_SITTER_APPLY S JOIN TBL_USER U ON S.USER_NUMBER = U.USER_NUMBER WHERE APPLY_STATUS = '승인 대기' ORDER BY APPLY_NUMBER DESC )
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RN > (#{page} - 1) * #{amount}

mapper.update:
UPDATE TBL_SITTER_APPLY
SET APPLY_STATUS='승인 완료'
WHERE APPLY_NUMBER = #{applyNumber}

UPDATE TBL_SITTER_APPLY
SET APPLY_STATUS='승인 거절'
WHERE APPLY_NUMBER = #{applyNumber}

mapper.select:
SELECT USER_NUMBER, SITTER_NUMBER, USER_ID, PET_FIELD_NAME, USER_PHONE_NUMBER ,REVIEW_COUNT, REVIEW_AVG FROM( SELECT ROWNUM RN, USER_NUMBER, SITTER_NUMBER, USER_ID, PET_FIELD_NAME, USER_PHONE_NUMBER ,REVIEW_COUNT, REVIEW_AVG FROM( SELECT U.USER_NUMBER, S.SITTER_NUMBER, U.USER_ID, NVL( (SELECT LISTAGG(PET_FIELD_NAME , ', ') WITHIN GROUP (ORDER BY SITTER_FIELD_NUMBER) FROM TBL_SITTER_FIELD SF WHERE USER_NUMBER = U.USER_NUMBER) ,'없음' ) AS PET_FIELD_NAME, U.USER_PHONE_NUMBER ,COUNT(REVIEW_NUMBER) AS REVIEW_COUNT, NVL(AVG(REVIEW_SCORE), 0) AS REVIEW_AVG FROM TBL_USER U JOIN TBL_SITTER S ON U.USER_NUMBER =S.USER_NUMBER LEFT JOIN TBL_SITTER_REVIEW SR ON S.SITTER_NUMBER = SR.SITTER_NUMBER GROUP BY S.SITTER_NUMBER, U.USER_ID, U.USER_PHONE_NUMBER, U.USER_NUMBER ORDER BY S.SITTER_NUMBER DESC ) B1
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RN > ( #{criteria.page} - 1 ) * #{criteria.amount}

SELECT USER_NUMBER, SITTER_NUMBER, USER_ID, PET_FIELD_NAME, USER_PHONE_NUMBER ,REVIEW_COUNT, REVIEW_AVG FROM( SELECT ROWNUM RN, USER_NUMBER, SITTER_NUMBER, USER_ID, PET_FIELD_NAME, USER_PHONE_NUMBER ,REVIEW_COUNT, REVIEW_AVG FROM( SELECT U.USER_NUMBER, S.SITTER_NUMBER, U.USER_ID, NVL( (SELECT LISTAGG(PET_FIELD_NAME , ', ') WITHIN GROUP (ORDER BY SITTER_FIELD_NUMBER) FROM TBL_SITTER_FIELD SF WHERE USER_NUMBER = U.USER_NUMBER) ,'없음' ) AS PET_FIELD_NAME, U.USER_PHONE_NUMBER ,COUNT(REVIEW_NUMBER) AS REVIEW_COUNT, NVL(AVG(REVIEW_SCORE), 0) AS REVIEW_AVG FROM TBL_USER U JOIN TBL_SITTER S ON U.USER_NUMBER =S.USER_NUMBER AND USER_ID LIKE '%' || #{keyword} ||'%' AND S.SITTER_STATUS ='Y' AND U.USER_STATUS = 2 LEFT JOIN TBL_SITTER_REVIEW SR ON S.SITTER_NUMBER = SR.SITTER_NUMBER GROUP BY S.SITTER_NUMBER, U.USER_ID, U.USER_PHONE_NUMBER, U.USER_NUMBER ORDER BY S.SITTER_NUMBER DESC ) B1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RN > ( #{criteria.page} - 1 ) * #{criteria.amount}

mapper.update:
UPDATE TBL_SITTER
SET SITTER_STATUS = #{sitterStatus}
WHERE SITTER_NUMBER = #{sitterNumber}
