mapper.update:
UPDATE TBL_STROLL_BOARD
SET STROLL_BOARD_VIEW_COUNT = STROLL_BOARD_VIEW_COUNT + 1
WHERE STROLL_BOARD_NUMBER = #{strollBoardNumber}

mapper.select:
SELECT STROLL_BOARD_NUMBER, STROLL_BOARD_TITLE, STROLL_BOARD_MEETING_DATE, STROLL_BOARD_ADMIN_DISTRICT, USER_NUMBER, USER_ID FROM ( SELECT ROWNUM RN, STROLL_BOARD_NUMBER, STROLL_BOARD_TITLE, STROLL_BOARD_MEETING_DATE, STROLL_BOARD_ADMIN_DISTRICT, USER_NUMBER, USER_ID FROM ( SELECT STROLL_BOARD_NUMBER, STROLL_BOARD_TITLE, TO_CHAR(STROLL_BOARD_MEETING_DATE, 'YYYY-MM-DD') AS STROLL_BOARD_MEETING_DATE, STROLL_BOARD_ADMIN_DISTRICT, B.USER_NUMBER, U.USER_ID FROM TBL_USER U JOIN TBL_STROLL_BOARD B ON U.USER_NUMBER = B.USER_NUMBER ORDER BY STROLL_BOARD_NUMBER DESC )
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RN > (#{page} - 1) * #{amount}

SELECT STROLL_BOARD_NUMBER, STROLL_BOARD_TITLE, TO_CHAR(STROLL_BOARD_MEETING_DATE, 'YYYY-MM-DD') AS STROLL_BOARD_MEETING_DATE, STROLL_BOARD_ADMIN_DISTRICT, USER_ID FROM ( SELECT ROWNUM RNUM, STROLL_BOARD_NUMBER, STROLL_BOARD_TITLE, STROLL_BOARD_MEETING_DATE, STROLL_BOARD_ADMIN_DISTRICT, USER_ID FROM ( SELECT S.STROLL_BOARD_NUMBER, S.STROLL_BOARD_TITLE, S.STROLL_BOARD_MEETING_DATE, S.STROLL_BOARD_ADMIN_DISTRICT, U.USER_ID FROM TBL_STROLL_BOARD S JOIN TBL_USER U ON U.USER_NUMBER = S.USER_NUMBER WHERE U.USER_NUMBER = #{userNumber} ORDER BY STROLL_BOARD_NUMBER DESC ) B1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) B2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}
