mapper.select:
SELECT STROLL_REPLY_NUMBER, STROLL_REPLY_CONTENT,
STROLL_REPLY_UPDATE_DATE, STROLL_BOARD_NUMBER, USER_NUMBER, USER_ID
FROM(
SELECT ROWNUM RN,
STROLL_REPLY_NUMBER, STROLL_REPLY_CONTENT, STROLL_REPLY_REGISTER_DATE,
STROLL_REPLY_UPDATE_DATE, STROLL_BOARD_NUMBER, USER_NUMBER, USER_ID
FROM(
SELECT STROLL_REPLY_NUMBER, STROLL_REPLY_CONTENT, STROLL_REPLY_REGISTER_DATE, STROLL_REPLY_UPDATE_DATE, STROLL_BOARD_NUMBER, R.USER_NUMBER, USER_ID
FROM TBL_USER U JOIN TBL_STROLL_REPLY R
ON U.USER_NUMBER = R.USER_NUMBER AND STROLL_BOARD_NUMBER = #{strollBoardNumber}
ORDER BY STROLL_REPLY_NUMBER DESC
)

WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}

)
WHERE RN > ( #{criteria.page} - 1 ) * #{criteria.amount}

mapper.update:
UPDATE TBL_STROLL_REPLY
SET STROLL_REPLY_CONTENT = #{strollReplyContent} ,STROLL_REPLY_UPDATE_DATE = SYSDATE
WHERE STROLL_REPLY_NUMBER = #{strollReplyNumber}
